import React, {useState, useEffect, useRef} from 'react';
import './UI/Questions.css';

const Questions = () => {

    const [maxHeight, setMaxHeight] = useState(null);
    const edgeDescRef = useRef(null);

    const handleToggle = (event) => {
      const edgeDesc = edgeDescRef.current;
      const edgeArrow = event.target.querySelector('.edge__arrow');

      if (maxHeight) {
        setMaxHeight(null);
        edgeArrow.style.transform = "rotate(0deg)";
      } else {
        setMaxHeight(edgeDesc.scrollHeight + "px");
        edgeArrow.style.transform = "rotate(-180deg)";
      }
    };


    // const [selected, setSelected] = useState(null);
