import React, {useState, useEffect, useRef} from 'react';
import './UI/Questions.css';

const Questions = () => {

    const [maxHeight, setMaxHeight] = useState(null);
    const questionsDescRef = useRef(null);

    const handleToggle = (event) => {
      const questionsDesc = questionsDescRef.current;

      if (maxHeight) {
        setMaxHeight(null);
      } else {
        setMaxHeight(questionsDesc.scrollHeight + "px");
      }
    };


    const [selected, setSelected] = useState(null);
    // const [heightEl, setHeightEl] = useState();

    // const refHeight = useRef();

    // useEffect(() => {
    //     setHeightEl(`${refHeight.current.scrollHeight}px`);
    // }, []);

    const toggle = (i) => {
        if (selected === i) {
            return setSelected(null);
        }

        setSelected(i)
    }

    return (
        <div className="questions__content">
            {Array.from(document.querySelectorAll('.questions__header')).map((el, index) => (
                <div key={index} className="questions__header" onClick={handleToggle}>
                  {el.textContent}
                </div>
            ))}
            <div ref={questionsDescRef} className="questions__desc" style={{ maxHeight: maxHeight }}>
              {/* questions description content */}
              desc
            </div>
        </div>
        
    );
};

