import React from 'react';
import './UI/time.css';

const Time = () => {

    let date = new Date();
    let day = date.getDate();
    let month = date.getMonth();
    let year = date.getFullYear();
    let hour = date.getHours();
    let min = date.getMinutes();
    let time = document.querySelector('.time');
    let dateHTML = document.querySelector('.date');
    time.innerHTML = `${addZero(hour)}:${addZero(min)}`
    timeDate.innerHTML = `${addZero(day)}.${addZero(month)}.${year} <span>${addZero(hour)}:${addZero(min)}:${addZero(sec)}`;
    function addZero(n) {
        return ((n < 10) ? '0' : '') + n;
    }
    

    return (
        <div>
            <div className='timeDate'>
                <div className='time'>12:53</div>
                <div className='date'>23.07.2023</div>
            </div>
        </div>
    );
}

export default Time;
